	private static <T extends MobEntity & IAnimatable> void defaultAnimatedMob(EntityType<T> entityType, String assetSubpath) {
		defaultAnimatedMob(entityType, assetSubpath, assetSubpath);
	}

	private static <T extends MobEntity & IAnimatable> void defaultAnimatedMob(EntityType<T> entityType, String modelSubpath, String textureSubpath) {
		defaultAnimatedMob(entityType, modelSubpath, textureSubpath, modelSubpath);
	}

	private static <T extends MobEntity & IAnimatable> void defaultAnimatedMob(EntityType<T> entityType, String modelSubpath, String textureSubpath, String animationSubpath) {
		animatedMob(entityType, shadow -> new AnimatedMobRenderer<T>(renderManager, new EntityGeoModel<T>(modelSubpath, textureSubpath, animationSubpath), shadow));
	}

	private static <T extends MobEntity> void animatedMob(EntityType<T> entityType, Function<Float, GeoEntityRenderer> renderer) {
		renderManager.register(entityType, renderer.apply(entityType.getWidth() / 3f));
	}
	
	private static void registerMobRenderer(EntityType<? extends MobEntity> entityType, EntityModel<MobEntity> model, ResourceLocation texture) {
		registerMobRenderer(entityType, model, 1f, texture);
	}

	private static void registerMobRenderer(EntityType<? extends MobEntity> entityType, EntityModel<MobEntity> model, float scale, ResourceLocation texture) {
		renderManager.register(entityType, new AoAMobRenderer(renderManager, model, entityType.getWidth() / 3f, scale, texture));
	}

	private static <T extends Entity> void register(EntityType<T> entityType, EntityRenderer<? super T> renderer) {
		renderManager.register(entityType, renderer);
	}

	private static  <T extends Entity> void animatedProjectileRenderer(EntityType<T> entityType, Function<Float, GeoProjectilesRenderer> renderer) {
		renderManager.register(entityType, renderer.apply(entityType.getWidth() / 3f));
	}
